package com.assignment.assignment.controller;import com.assignment.assignment.entity.UserData;import com.assignment.assignment.entity.UserDto;import com.assignment.assignment.service.UserDataService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;import java.util.List;@RestController@RequestMapping("/userData")public class UserDataController {    @Autowired    UserDataService userDataService;    @PostMapping("/save")    public ResponseEntity<String> userDataSave(@RequestBody UserData data) {        String s = userDataService.saveData(data);        return new ResponseEntity<>(s, HttpStatus.OK);    }    @GetMapping("/getAllData")    public ResponseEntity<List<UserData>>getAllUserData(){        List<UserData> allData = userDataService.getAllData();        return new ResponseEntity<>(allData,HttpStatus.OK);    }    @PutMapping("/updateEmail")    public ResponseEntity<String>updateEmail(@RequestBody UserDto userDto){        String s = userDataService.updateUserEmail(userDto);        return new ResponseEntity<>(s,HttpStatus.OK);    }    @DeleteMapping("/deleteUser/{id}")    public ResponseEntity<String>deleteUser(@PathVariable("id") Long id){        String s = userDataService.deleteUser(id);        return new ResponseEntity<>(s,HttpStatus.OK);    }}