package com.assignment.assignment.service;import com.assignment.assignment.entity.UserData;import com.assignment.assignment.entity.UserDto;import com.assignment.assignment.repository.UserDataRepo;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;import java.util.Optional;@Servicepublic class UserDataService {    @Autowired    UserDataRepo userDataRepo;    public String saveData(UserData data) {            userDataRepo.save(data);            return "Data Save Successfully";        }    public List<UserData> getAllData(){       return userDataRepo.findAll();    }    public String updateUserEmail(UserDto userDto){        Optional<UserData> byId = userDataRepo.findById(userDto.getId());        if (byId.isEmpty()){            return "No User Id Found";        }else {            byId.get().setEmail(userDto.getEmail());            userDataRepo.save(byId.get());        }        return "Email Updated ";    }    public String deleteUser(Long id){        Optional<UserData> byId = userDataRepo.findById(id);        if (byId.isEmpty()){            return "No User Id Found";        }else {            userDataRepo.deleteById(id);        }        return "User Data Deleted For ID : "+id;    }}